  PRIMARY KEY (`id`),
  KEY `language` (`language`),
  KEY `articleid` (`articleid`),
  FULLTEXT KEY `title` (`title`,`body_plain`)
) ENGINE=MyISAM AUTO_INCREMENT=111 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
